'   select trunc(sysdate,''mm'') into l_first_day from dual;',
'   tc_timecards_pkg.week_start_end_date(l_first_day, l_week_start, l_week_end);',
'   :P3_RANGE_WEEK_START := l_week_start;',
'',
'elsif :P3_SELECT_RANGE = ''LM'' then  --last month',
'    select add_months(trunc(sysdate,''mm''), -1) into l_first_day  from dual;',
'    tc_timecards_pkg.week_start_end_date(l_first_day, l_week_start, l_week_end);',
'   :P3_RANGE_WEEK_START := l_week_start;',
