,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'l_emp_id    number;',
'',
'cursor approved_timesheets_c',
'is',
'select TIMESHEET_ID,',
'       EMP_ID,',
'       (select full_name from tc_employee te where  te.EMP_ID = ts.EMP_ID) Employee_name,',
'       (select project_name from tc_projects tp where tp.PROJECT_ID = ts.PROJECT_ID) project_name,',
'       (select task_name from tc_project_tasks tpt where tpt.TASK_ID = ts.TASK_ID) task_name,',
'       STATUS,',
'       WEEK_START,',
'       WEEK_END,',
'       MONDAY,',
'       TUESDAY,',
'       WEDNESDAY,',
'       THURSDAY,',
'       FRIDAY,',
'       SATURDAY,',
'       SUNDAY,',
'       TOTAL_HOURS',
'  from TC_TIME_SHEET ts',
'  where STATUS = ''Approved''',
'  and   WEEK_START = :P12_WEEK_START',
'  and   EMP_ID     = :P12_EMP_ID;',
'',
'begin',
'',
'   update tc_time_sheet ',
'   set    status = ''Approved'',',
'          last_update_date = sysdate,',
'          last_updated_by  = :APP_USER',
'   where  1 = 1',
'   and    EMP_ID  = :P12_EMP_ID',
'   and    WEEK_START = :P12_WEEK_START;',
'   --TIMESHEET_ID     = :TIMESHEET_ID;  ',
'',
'for i in approved_timesheets_c',
'loop',
'tc_timecards_pkg.submit_approved_timecard(i.EMP_ID,',
'                                          i.PROJECT_NAME,',
'                                          i.WEEK_START,',
'                                          i.WEEK_END,',
'                                          i.MONDAY,',
'                                          i.TUESDAY,',
'                                          i.WEDNESDAY,',
'                                          i.THURSDAY,',
'                                          i.FRIDAY,',
'                                          i.SATURDAY,',
'                                          i.SUNDAY,',
'                                          i.STATUS,',
'                                          i.TASK_NAME',
'                                              );',
'end loop;',
'',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(51991414330066892702)
);
